status_t debug_read_handler(
    uint8_t attribute_id,
    uint16_t first,
    uint16_t last,
    uint8_t *output,
    uint8_t output_len,
    uint8_t *bytes_written
) {
    status_t status = STATUS_SUCCESS;
    uint8_t bytes_to_write = 0;

    if (first > last) {
        return STATUS_COMMS_INVALID_ROWS;
    }

    switch (attribute_id) {
        
        case DEBUG_ATTRIBUTE_ENABLED_ID:
            if (last >= DEBUG_ATTRIBUTE_ENABLED_ROWS) {
                last = DEBUG_ATTRIBUTE_ENABLED_ROWS - 1;
            }
            bytes_to_write = sizeof(bool) * (last - first + 1);
            if (bytes_to_write > output_len) {
                return STATUS_COMMS_INSUFFICIENT_BUFFER_SPACE;
            }
            /* rows=4 read_callback= */
            memcpy(output, &debug_inst.enabled[first], bytes_to_write);
            break;
        
        default:
            return STATUS_COMMS_INVALID_ATTRIB_ID;
    }

    *bytes_written = bytes_to_write;
    return status;
}

status_t debug_write_handler(
    uint8_t attribute_id,
    uint16_t first,
    uint16_t last,
    uint8_t *input,
    uint8_t input_len
) {
    status_t status = STATUS_SUCCESS;
    uint8_t bytes_to_write = 0;

    if (first > last) {
        return STATUS_COMMS_INVALID_ROWS;
    }

    switch (attribute_id) {
        
        case DEBUG_ATTRIBUTE_ENABLED_ID:
            if (last >= DEBUG_ATTRIBUTE_ENABLED_ROWS) {
                last = DEBUG_ATTRIBUTE_ENABLED_ROWS - 1;
            }
            bytes_to_write = sizeof(bool) * (last - first + 1);
            if (bytes_to_write != input_len) {
                return STATUS_COMMS_INCORRECT_INPUT_DATA;
            }
            memcpy(&debug_inst.enabled[first], input, bytes_to_write);
            break;
            
        default:
            return STATUS_COMMS_INVALID_ATTRIB_ID;
    }

    return status;
}

