status_t nvm_read_handler(
    uint8_t attribute_id,
    uint16_t first,
    uint16_t last,
    uint8_t *output,
    uint8_t output_len,
    uint8_t *bytes_written
) {
    status_t status = STATUS_SUCCESS;
    uint8_t bytes_to_write = 0;

    if (first > last) {
        return STATUS_COMMS_INVALID_ROWS;
    }

    switch (attribute_id) {
        
        case NVM_ATTRIBUTE_CODEWORK_ID:
            bytes_to_write = sizeof(uint32_t);
            if (bytes_to_write > output_len) {
                return STATUS_COMMS_INSUFFICIENT_BUFFER_SPACE;
            }
            /* rows= read_callback= */
            memcpy(output, &nvm_inst.codework, bytes_to_write);
            break;
        
        default:
            return STATUS_COMMS_INVALID_ATTRIB_ID;
    }

    *bytes_written = bytes_to_write;
    return status;
}

status_t nvm_write_handler(
    uint8_t attribute_id,
    uint16_t first,
    uint16_t last,
    uint8_t *input,
    uint8_t input_len
) {
    status_t status = STATUS_SUCCESS;
    uint8_t bytes_to_write = 0;

    if (first > last) {
        return STATUS_COMMS_INVALID_ROWS;
    }

    switch (attribute_id) {
        
        case NVM_ATTRIBUTE_CODEWORK_ID:
            bytes_to_write = sizeof(uint32_t);
            if (bytes_to_write != input_len) {
                return STATUS_COMMS_INCORRECT_INPUT_DATA;
            }
            memcpy(&nvm_inst.codework, input, bytes_to_write);
            break;
            
        default:
            return STATUS_COMMS_INVALID_ATTRIB_ID;
    }

    return status;
}

status_t nvm_method_handler(
    uint8_t method_id,
    uint8_t *input,
    uint8_t input_len
) {
    status_t status = STATUS_ERROR;

    switch (method_id) {
        
        case NVM_METHOD_RESET_ID:
            status = nvm_reset();
            break;
            
        default:
            status = STATUS_COMMS_INVALID_METHOD_ID;
    }

    return status;
}
